import React, { useState } from "react";

export const Search = ({}) => {
  const [state, setState] = useState({
    userInput: ""
  });

  return (
    <>
      <div className="spacer" />
      <div className="container">
        <label className="input-label">What's you trash can's code?</label>
        <div className="field-and-button">
          <input
            type="text"
            className="input-field"
            value={state.userInput}
            onChange={e => setState({ userInput: e.target.value })}
          />
          <div style={{ width: "20px" }} />
          <button className="submit">Do It</button>
        </div>
      </div>
      <style jsx>{`
        .spacer {
          flex: 1;
        }
        .container {
          width: 500px;
          margin: auto;
          display: flex;
          flex-direction: column;
          align-items: center;
        }
        .field-and-button {
          display: flex;
        }
        .submit {
          flex: 1;
          font-size: 4vh;
          border-radius: 10px;
          border: none;
          background-color: rgba(0, 150, 0, 0.5);
          box-shadow: 0 5px 20px 0 rgba(0, 0, 0, 0.25);
          color: white;
        }
        .input-label {
          font-size: 3vh;
          padding: 20px;
        }
        .input-field {
          flex: 5;
          width: 100%;
          padding: 10px;
          font-size: 4vh;
          text-align: center;
          border-radius: 10px;
          border: none;
          box-shadow: 0 5px 20px 0 rgba(0, 0, 0, 0.25);
        }
        .input-field:focus {
          outline: none;
          box-shadow: 0 5px 30px 0 rgba(0, 0, 0, 0.5);
          transition: 0.3s ease all;
        }
      `}</style>
    </>
  );
};

export default Search;
