import React, { useState } from "react";

export const Search = ({}) => {
  const [state, setState] = useState({
    userInput: ""
  });

  return (
    <>
      <div className="spacer" />
      <div className="container">
        <label className="input-label">What's you trash can's code?</label>
        <input
          type="text"
          className="input-field"
          value={state.userInput}
          onChange={e => setState({ userInput: e.target.value })}
        />
        <button>GO!</button>
      </div>
      <style jsx>{`
        .spacer {
          flex: 1;
        }
        .container {
          width: 500px;
          margin: auto;
          display: flex;
          flex-direction: column;
          align-items: center;
        }
        .input-label {
          font-size: 3vh;
          padding: 20px;
        }
        .input-field {
          width: 100%;
          padding: 10px;
          font-size: 4vh;
          text-align: center;
          border-radius: 10px;
          border: none;
          box-shadow: 0 5px 20px 0 rgba(0, 0, 0, 0.25);
        }
        .input-field:focus {
          outline: none;
          box-shadow: 0 5px 30px 0 rgba(0, 0, 0, 0.5);
          transition: 0.3s ease all;
        }
      `}</style>
    </>
  );
};

export default Search;
